node.nlcode:
[%
my $str;
my $csr= $DB->sqlSelectMany("*", "coolwriteups", "", "order by tstamp desc limit 16");
my $xml;
use XML::Generator;
my $XG = new XML::Generator;
my $count = 0;
my $row = "";
while (my $CW = $csr->fetchrow_hashref()) {
  my $COOL =getNodeById($$CW{coolwriteups_id});
  next unless $COOL;
  my $CPARENT = getNodeById $$COOL{parent_e2node}, 'light';
  $row.="<TD align=center>".linkNode($COOL, $$CPARENT{title}, {lastnode_id => 0}) ."</td>";
  if ($count % 2) {
    $row = "<tr>$row</tr>";
    $row .="<TR><td bgcolor=#000000 colspan=2>".htmlcode('clearimage','0,1')."</td></tr>";
    $str .= $row;
    $row = "";
  }  
  my $author_user = getNodeById($$COOL{author_user});
  $author_user = ($author_user ? $$author_user{title} : "");
  my $cooledby_user = getNodeById($$CW{cooledby_user});
  $cooledby_user = ($cooledby_user ? $$cooledby_user{title} : "");
  $xml.=
     "\t"
     . $XG->cooled(
          {
               node_id => $$COOL{node_id}
               , parent_e2node => $$COOL{parent_e2node}
               , author_user => $author_user
               , cooledby_user => $cooledby_user
          }
          , $$CPARENT{title}
       )
       . "\n"
       ;
  $count++;
}
my $SETTINGS = getVars(getNode('system settings','setting'));
$xml = $XG->COOLEDNODES(
   $XG->INFO({site => $$SETTINGS{site_url}, sitename => $$SETTINGS{site_name},  servertime => scalar(localtime(time))}, 'Rendered by the Cool Nodes XML Ticker')
   . "\n".$xml . "\n");

my $XNODE = getNode('cool nodes XML ticker', 'fullpage');
$$XNODE{doctext} = $xml;
updateNode($XNODE, -1);


$str;
%]
