node.code:
my $author_link = getNodeById($$args{author_id}) ?
  linkNode($$args{author_id})
  : "(who has been unpersoned) "
  ;

my $wu_link = getNodeById($$args{writeup_id}) ?
  "titled " . linkNode($$args{writeup_id})
  : " which has since been removed"
  ;


return "A new user, <b>$author_link</b>, posted a writeup $wu_link.";

node.invalid_check:
use DateTime;
use DateTime::Format::Strptime;
my $dateParser = new DateTime::Format::Strptime(
  	pattern => '%F %T',
  	locale  => 'en_US',
  );
my $target_wu = getNodeById($$args{writeup_id});
my $isDraft = $$target_wu{type}{title} eq "draft";
my $notifyPublishTime = $dateParser->parse_datetime($$args{publish_time});
my $writeupPublishTime = $dateParser->parse_datetime($$target_wu{publishtime});
# Tack an extra few seconds since there's a race between
#  writeup publish and notification being added, and they
#  presently occur in reverse order
$notifyPublishTime->add( seconds => 10) if defined $notifyPublishTime;
my $isOld = $notifyPublishTime ?
   DateTime->compare( $notifyPublishTime, $writeupPublishTime ) < 0
   : 0
   ;
my $isInvalid = !$target_wu || $isDraft || $isOld;
return $isInvalid;
