node.code:
return unless isGod($USER);
my $U = $query->param('bestow_id');
getRef $U;

$$U{votesleft} += 25;
$$U{karma} += 1;

my $bestow = getNode("bestow","opcode");

$DB->sqlInsert('seclog', {'seclog_user' => $$USER{node_id}, 'seclog_node' => $$bestow{node_id}, 'seclog_details' => "$$U{title} was given 25 votes by $$USER{title}"});

updateNode($U, -1);
$U = getNodeById(getId($U), 'force');
if (getId($U) == getId($USER)) { $USER = $U ; }
