node.code:
return unless isGod($USER);
my $U = $query->param('bless_id');
$U = getNode($query->param("node"), 'user') if ($query->param('node'));
getRef $U;

return unless $$U{type}{title} eq 'user';

my $gp = int($query->param('experience'));
$gp ||= 10;

$$U{karma} += 1;
adjustGP($U, $gp);

my $bless = getNode("bless","opcode");
$DB->sqlInsert('seclog', {'seclog_user' => $$USER{node_id}, 'seclog_node' => $$bless{node_id}, 'seclog_details' => "$$U{title} was blessed 10GP by $$USER{title}"});

my $from =  "!";
htmlcode('sendPrivateMessage'
	, {
		'author_id' => getId(getNode('Cool Man Eddie', 'user')),
		'recipient_id' => $$U{user_id},
		'message' => "Whoa, you&rsquo;ve just been [bless|blessed]$from"
	}
);

updateNode($U, -1);
htmlcode('achievementsByType', 'karma', $$U{user_id});
