node.code:
return '' if ($$USER{user_id} == $HTMLVARS{guest_user});

if($query->param('amount')) {
	my $amount = $query->param('amount');
	if($amount =~ /^(\d+)$/) {
		$amount=$1;
		$amount = 50 if $amount > 50 ;
		$$VARS{num_newwus}=$amount;
	}
}

if ( $query->param( 'nw_nojunk' ) ) {
	$$VARS{ nw_nojunk } = 1 ;
} else {
	delete $$VARS{ nw_nojunk } ;
}

return 1;
