node.code:
my $uName = $query->param("new_leader");
my $userGroup = $query->param("node_id");

return unless $uName;
return unless $userGroup;

my $recUser = getNode($uName,"user");
if (!$recUser) {
	$uName =~ s/\_/ /gs;
	$recUser = getNode($uName, 'user');
}

return unless $recUser;

return unless htmlcode('isinUsergroup', "$$recUser{node_id},$userGroup");

my $ugOwners = getNode('usergroup owners','setting');

my $ugOwner = getVars $ugOwners;

$$ugOwner{$userGroup} = $$recUser{node_id};

setVars($ugOwners, $ugOwner);

return 1;
