node.code:
return if(!exists($HTMLVARS{'group_gods'}{$$USER{node_id}})
&& !exists($HTMLVARS{'group_content editors'}{$$USER{node_id}}));

my $notetext = $query->param('notetext'); 
my $notefor = int($query->param('notefor'));

my $NOTEFOR = getNodeById($notefor);
return unless $NOTEFOR;

#Strip dynamic URLs
$notetext =~ s/\<.*?img.*?src[\s\"\']*?\=[\s\"\']*?.*?\?.*?\>//g;

my $recentNotes_id = getId(getNode("Recent Node Notes","oppressor_superdoc"));

foreach($query->param) {
	if($_ =~ /^deletenote\_(\d+)$/) {
		$DB->sqlDelete('nodenote', "nodenote_id=$1");
		$DB->sqlInsert("seclog", {
	   'seclog_node'=>"$recentNotes_id",'seclog_user'=>$$USER{user_id},
	   'seclog_details'=>"removed note on [$$NOTEFOR{title}]"});
	}
}

htmlcode('addNodenote', $notefor, $notetext, $USER) if $notetext;
