node.doctext:
[{openform}]
<input type="text" name="ftsearch" ><select name="searchcombine"><option value="OR">Any of these words (OR)</option><option value="AND">All of these words (AND)</option></select><br>
<br><input type="submit" value="Full text search"></form>
[%
  my $TEXT = $query->param("ftsearch");
  my $str;
  return "Enter some words to search for<br>" unless $TEXT;

  my $dbs = getVars(getNode("fulltextdb settings","setting"));
  my $ftdb = DBI->connect("DBI:mysql:$$dbs{database}:$$dbs{hostname}", "$$dbs{username}", "$$dbs{password}");
  $ftdb or return "Ack! can't connect to ftdb<br>";

  $TEXT =~ s/[^0-9A-Za-z\@\-\_\s]//g;
  $TEXT =~ s/[\r\n]/ /g;
  $TEXT =~ s/\s+/ /g;

  my $sc = $query->param("searchcombine");
  $sc = undef unless($sc eq "AND" or $sc eq "OR");
  $sc ||= "OR";

  my @words = split " ",$TEXT;
  my $temptxt = "";

  my $RESULTS;
  my $ANDRESULTS; 
  my $pass = 0;
  my $CW = getVars(getNode("Common Words", "setting"));


  foreach my $w (@words)
  {
     if(length($w) < 4)
     {
        $str.="<small>$w - too short</small><br>";
        next;
     } 

     if($$CW{$w})
     {
        $str.="<small>$w - the word is too common and has been omitted</small><br>";
        next;
     }

     my $csr = $ftdb->prepare("SELECT ftdict_id FROM ftdict WHERE ftdict_word=".$ftdb->quote("$w")." LIMIT 1");
     $csr->execute();
     my $row = $csr->fetchrow_hashref();
     unless($row){
        $str.="<small>$w - no results available</small><br>";
        next;
     }

     my $dictid = $$row{ftdict_id};

     $csr = $ftdb->prepare("SELECT ftcache_cache from ftcache where ftcache_dictid=$dictid");
     $csr->execute();
     $row = $csr->fetchrow_hashref();
     unless($$row{ftcache_cache}){
        $str.="<small>$w - no results returned</small><br>";
        next;
     }

     $str.="<small>$w - results found</small><br>";
     $temptxt="$$row{ftcache_cache};";


     foreach(split(";",$temptxt))
     {
       my @vals = split("-", $_);
       if($sc eq "AND")
       {
          $$ANDRESULTS{$vals[0]} = ($vals[1]+$$RESULTS{$vals[0]}) if $$RESULTS{$vals[0]};
       }

       $$RESULTS{$vals[0]} ||= 0;
       $$RESULTS{$vals[0]} += $vals[1];
     }

     $RESULTS = $ANDRESULTS if($sc eq "AND" and $pass != 0);
     $pass++;
   }

  my $startat = $query->param("startat");
  $startat =~ s/[^0-9]//g;
  $startat ||= 0;

  my @final = sort { $$RESULTS{$b} <=> $$RESULTS{$a}} keys %$RESULTS;
  my $cnt = $#final;

  if($cnt == -1){
    $ftdb->disconnect(); 
    return "$str<br>No results found";
  }
  my $highend = $startat+50;
  $highend = $cnt if $highend > $cnt;
  my $lowend = $startat; 
  $lowend=1 if $lowend==0;

  $str.="<strong>$cnt</strong> results total, currently viewing results <strong>$lowend</strong> through <strong>$highend</strong><br>";
  $str.="<br><p align=\"center\"><hr width=\"300\"></p>";
  $str.="<table>";

  for($startat..($startat+50))
  { 
     my $n = getNodeById($final[$_]);
     next unless $n;
     next unless $$n{type}{title} eq "writeup";
     my $e2n = getNodeById($$n{parent_e2node});
     $str.="<tr><td>".linkNode($n, $e2n->{title})." (".linkNode($e2n, "full").") by ".linkNode($$n{author_user})."</td></tr>";
     $str.="<tr><td><blockquote>";
     my $txt = substr($$n{doctext},0,300);
     $txt =~ s/\<.*?\>//g;
     $txt =~ s/[\[\]\|]//g;
     $txt =~ s/\&.*?\;//g;
     $str.="<small>$txt</small>";
     $str.="</blockquote></td></tr>";
  }
  $str.="</table>";
  $ftdb->disconnect();


 my $firststr = (($startat-50 < 1)?(1):($startat-50))."-".(($startat==0)?(50):($startat));
 $str.="<p align=\"center\"><table width=\"70%\"><tr>";
  $str.="<td width=\"50%\" align=\"center\">";
  if(($startat-50) >= 0){
     $str.=linkNode($NODE,$firststr,{"startat" => ($startat-50), "ftsearch" => $query->param("ftsearch")});
  }else{
     $str.=$firststr;
  }
  $str.="</td>";
  $str.="<td width=\"50%\" align=\"center\">";
  my $secondstr = ($startat+50)."-".(($startat + 100 < $cnt)?($startat+100):($cnt));

  if(($startat+50) <= ($cnt)){
     $str.=linkNode($NODE,$secondstr,{"startat" => ($startat+50), "ftsearch" => $query->param("ftsearch")});
  }else{
     $str.="(end of list)";
  }

  $str.="</td>";
  $str.="</tr></table></p>";
  return "$str";


%]
