node.doctext:
[%
no strict;

my $str; 
my @modules = (
	'Everything',
	'Everything::HTML',
	'Everything::XML',
	'Everything::NodeBase',
	'Everything::Experience',
	'Everything::Room',
	'Everything::Search',
	'Everything::MAIL',
	'Everything::CacheStore',
	'CGI::Param',
	'Everything::E2Custom',
);

foreach my $modname (@modules) {
		local *stash;
		*stash = *{ "${modname}::" };
		foreach(keys %stash) {
			$str.=$modname. "- $_<br />";
		}
}

# uncommenting this line returns everything, which is a lot!
# return $str;

$str = "";

$str.=htmlcode('openform');
$str.="<select name='module'>";
foreach my $modname (@modules) {
$str.="<option value='$modname'>$modname</option>";
}
$str.="</select>";
$str.=htmlcode('closeform');

my $parentmod = $query->param('module') || "Everything";

my $parentfile;
my @mod;

foreach (@INC)
{
   $parentfile = "$_\/".$parentmod.".pm";
   $parentfile =~ s/\:\:/\//g;
   open MODULE, $parentfile or next;
   @mod = <MODULE>;
   close MODULE;
   last;
}


if (@mod) {
	$str.= "module $parentmod loaded: ".int(@mod)." lines\n";
}

$str .= "<p><b>Code Result:</b></p>";
$str .= listCode(join('', @mod));

$str;

%]
