node.doctext:
[{openform}]
User [% $query->textfield("myuser", "", 30) %]
gets [% $query->textfield("mycools", "", 3, 3) %]
cools.
[{closeform}]
<p>
[%
my $user = $query->param("myuser");
my $cools = $query->param("mycools");

my $UID = getId($USER)||0;
my $isRoot = exists $HTMLVARS{group_gods}{$UID};

return unless $isRoot;

return unless $user and $cools;
return "invalid cools" unless $cools =~ /^\d+$/;


my $U = getNode($user, 'user');
return "<i>No user \"$user\" exists!</i>" unless $U;

my $V = getVars $U;
$$V{cools} = $cools;
setVars($U, $V);
"<i>$cools cools were bestowed to ".linkNode($U)."</i>";
  $DB->sqlInsert('seclog', {'seclog_user' => $$USER{node_id}, 'seclog_node' => $$NODE{node_id}, 'seclog_details' => "$$U{title} was given $cools cools by $$USER{title}"});
$$U{karma}+=1;
updateNode($U, -1);

return 'Success!';
%]
