node.doctext:
[{openform}]
[%
  my $u = $query->param("christen"); 
  my $U = getNode($u, 'user');
  $U = "" if $$U{title} eq 'bones';
  return $query->textfield("christen") . " is now ".$query->textfield("newname") unless $U;
  return "no" if isGod($U); 
  
  my $newname = $query->param("newname");
  my $OLDU = getNode($newname,'user');
  $OLDU = "" if $$U{title} eq 'bones';
  return "Need to kill ".linkNode($OLDU). " before you can do this"
	if $OLDU;
  
  #rename the user
  my $str = "$$U{title} is now [$newname]";
  $$U{title} = $newname;
  updateNode($U, $USER);
  parseLinks($str);
%]

[{closeform}]
