node.doctext:
[%
use Everything::File;
my $god = isGod($USER);
my $str = "";
my $file = new Everything::File($query->param("filename"));
my $filename = $file->getRelativeFilename();

if($god || (not ($filename =~ /\.\./)))
{
  my $parent = new Everything::File($file->getParentDir());

  $str .= $query->start_multipart_form("POST",
    $ENV{script_name}) . "\n";
  $str .= $query->hidden("displaytype") . "\n";
  $str .= $query->hidden("node_id", getId $NODE) . "\n";

  $str .= "File: ";
  $str .= $query->textfield("filename", undef, 40);
  $str .= $query->submit('go', 'Go') . "\n";
  $str .= $query->end_form;

  $str .= "<p>" .
    $parent->linkFile(".. Parent Directory", 0, $god) . "\n";

  if($file->isDirectory())
  {
    my $filename;
    my $subfile;
    my @sortedFiles;

    opendir DIR, $file->getFullFilename();
    my @files = readdir DIR;
    closedir DIR;

    @sortedFiles = sort @files;
    $str .= "<ul>\n";
    foreach my $f (@sortedFiles)
    {
      next if($f eq ".." or $f eq ".");
      $filename = Everything::File::genFilename(
        $file->getFullFilename(), $f);
      $subfile = new Everything::File($filename);
      $str .= "<li>";
      $str .= $subfile->linkFile($f, 0, $god);
      $str .= "/" if(-d $filename);
      $str .= "</li>\n";  
    }
    $str .= "</ul>\n";
  }
  else
  {
    if(not ($file->isText()))
    {
      my $type = $file->getFileType();
      my $url = $file->linkFileRaw("filedata", $god);

      $str .= "<p><font size=2>Can't edit file. ";
      $str .= "Binary data</font><br>";

      $str .= (($type =~ /image/) ? "<img src=" : "<a href=");
      $str .= $url . ">";
      $str .= "Download this file</a>"
        if(not ($type =~ /image/));
    }
    else
    {
      my $contents = encodeHTML($file->read(), 1);

      $str .= "<p>";
      $str .= $file->linkFile("Edit this file", 1, $god);
      $str .= "<table border=1 width='75%'><tr><td>\n";
      $str .= "<pre>\n" . $contents . "\n</pre>\n";
      $str .= "</td></tr></table>\n";
    }
  }
}
else
{
  $str .= "<p><b>Forbidden!</b>  You are not allowed to ";
  $str .= "view this file.\n";
}

$str;
%]
