node.doctext:
[%
  my $lvxp = getVars(getNode('level experience', 'setting'));
  my $lvwu = getVars(getNode('level writeups', 'setting'));


  my $V = getVars(getNode('level weenies', 'setting'));
  my $str;
  my @names = keys %$V;
 @names = reverse(sort {$$V{$a} <=> $$V{$b}} @names);

  foreach (@names) {
    $str.="<li>[$_] was granted $$V{$_}...";

    my $usr = getNode($_, 'user');
    my $xp = $$usr{experience};
    my $wus = $DB->sqlSelect("count(*)", "node", "type_nodetype=117 and author_user=$$usr{user_id}");

    my $xpfor = 0;
    my $wufor = 0;
    my $curlv = 0;

    for(my $i=2; $i<=13; $i++)
    {
       if($$lvxp{$i} > $xp){$xpfor = $i-1; $i = 15;};
       $xpfor = 13 if($i == 13);
    }
    for(my $i=2; $i<=13; $i++)
    {
       if($$lvwu{$i} > $wus){$wufor = $i-1; $i = 15;};
       $wufor = 13 if($i == 13);
    }
  
    $curlv = $wufor;
    $curlv = $xpfor if ($wufor > $xpfor);

    $str.= "<small>and has currently enough writeups for Level $wufor and enough XP for Level $xpfor, making them Level $curlv.</small>";

    $str .= " (unaffected)" if($$V{$_} <= $curlv);
    $str .= "\n"; 
}
$str;
%]
