node.doctext:
[%
no strict;
my $str = "";

sub dumpvar {
  my ($packageName) = @_;

  local *alias;

  *stash = *{"${packageName}::"};

  # Iterate through the symbol table, which contains glob
  # values indexed by symbol names.
  while (($varName, $globValue) = each %stash) {
    $str .= "$varName =============================<br>\n";
    *alias = $globValue;
    if (defined ($alias)) {
      $str .= "\$$varName $alias <br>\n";
    } 
    if (defined (@alias)) {
      $str .= "\@$varName @alias <br>\n";
    } 
    if (defined (%alias)) {
      $str .= "\%$varName " . %alias . "<br>\n";
    }
  }
}

dumpvar("Everything");

$str;
%]
