node.doctext:
[%

return unless $$USER{title} eq 'mauler';

my $str;

if ($query->param('bountificate')) {

my $recipient = $query->param('noder');
my $number = $query->param('number');
my $user = getNode($recipient, 'user');

my $v = getVars($user);
$$v{Bounty} = 1;
$$v{BountyNumber} = $number;
setVars($user, $v);

$str = "<p>User [" . $$user{title} ."] has been set as having a bounty and has been given a bounty number of $number. Would you like to [Bountificator|do another]?</p>";
}

$str.=$query->start_form();
$str.=$query->hidden('node_id', $$NODE{node_id});
$str.= "</p><p>Which noder has earned your favor? " . $query->textfield('noder');
$str.= "</p><p>What is their bounty number " . $query->textfield('number');
$str.=$query->submit('bountificate','Bountificate!');
$str.=$query->end_form();

%]
