node.doctext:
[%

use Time::HiRes;
use String::Similarity;
my @start = Time::HiRes::gettimeofday; # Why?? - Oolong
my $timeStr;
my $timeCount = 1;

my $str;
my $title = $query->param('node');
my $lnode = $query->param('lastnode_id');
$lnode ||= '0';

return htmlcode('randomnode','Psst! Over here!') unless $title;
$str .= 'Here\'s the stuff we found when you searched for "'.$title.'"';

$str .= "\n\t<ul>";


my $isRoot = exists $HTMLVARS{'group_gods'}{$$USER{node_id}};
my $curType;

my @nodes;
#For some reason, sometimes e2 thinks there is no nodegroup here. Huh? --[Swap]
if(defined $$NODE{group}) {
 @nodes = @{ $$NODE{group} };
}

my @e2node_ids = map ($_ -> {node_id},
                      grep ($_ -> {type}{title} eq "e2node",
                            @nodes)
                      );

my %fillednode_ids;

#Only make one SQL call to find the non-nodeshells.
if(@e2node_ids){
  my $sql = "SELECT DISTINCT nodegroup_id
             FROM nodegroup
             WHERE nodegroup_id IN ("
             .join(", ", @e2node_ids).")";

  \@fillednode_ids{  @{$dbh -> selectcol_arrayref($sql)}  };
}

foreach my $ND (@{ $$NODE{group} }) {
	#$ND = getNodeById($ND, 'light');
	next unless canReadNode($USER, $ND);
	$curType = $$ND{type}{title};

	next if $curType eq 'writeup';
	next if $curType eq 'debatecomment';

	if ($curType eq 'debate' and not $isRoot) {
		next unless htmlcode('isinUsergroup',"$$USER{node_id},$$ND{restricted}");
	}
	unless ($curType eq 'e2node' &! exists $fillednode_ids{$$ND{node_id}} && $$VARS{hidenodeshells}) {
		my $openli = "<li>";
		# Mark nodeshells with class name
		if ($curType eq 'e2node') {
			$openli = '<li class="nodeshell">' 
	        		unless exists $fillednode_ids{$$ND{node_id}};
		}
		if ($$USER{user_id} == $HTMLVARS{guest_user}){
			$str .= $openli . linkNode($ND, '', {lastnode_id=>0}) ;
		}
		else {
			$str .= $openli . linkNode($ND, '', {lastnode_id=>$lnode}) ;
		}
		if($curType ne 'e2node'){
			$str .= " ($curType)";
		}
		#$str .= "($ND->{node_id})" if $HTMLVARS{'group_gods'}{$USER->{node_id}};
		$str .= " similarity ".similarity($title, $$ND{title});
		$str .= "</li>\n";
	}
#	$str .= '<tr><td>';
#	$str .= linkNode($ND, '', {lastnode_id=>$lnode}) . '</td><td>';
#	$str .= ($curType eq 'e2node' ? '' : '('.$curType.')') . '</td><td>';
#	$str .= htmlcode('parsetimestamp',$$ND{createtime}); #this may be too much; maybe just the date? or even just month and year?
#	$str .= "</td></tr>\n";

}

$str .= "</ul>\n";

$str
#.'</table>'
;
%]
[{e2createnewnode}]
