node.doctext:
[%
use Everything::File;
my $str = "";
my $file = new Everything::File($query->param("filename"));
my $parent = new Everything::File($file->getParentDir());
my $content = $query->param("filecontents");

if(defined $content)
{
  $str .= "<h3><font color=red>" .
    "Warning: Unable to write file!</font></h3>\n"
    unless($file->write($content));
}

$str .= $query->start_multipart_form("POST",
  $ENV{script_name}) . "\n";
$str .= $query->hidden("displaytype") . "\n";
$str .= $query->hidden("node_id", getId $NODE) . "\n";
$str .= $query->hidden("filename", $file->getFilename) . "\n";
$str .= "<p>" . $parent->linkFile("Parent Directory") . "\n";


if($file->isText())
{
  $str .= "<p>Edit the file:<br>\n";
  $str .= $query->textarea("filecontents", $file->read(), 20 ,60);
}
else
{
  $str .= "<p>Unable to edit: not a text file</p>\n";
}

$str .= $query->submit('submit', 'Submit') . "\n";

$str;

%]
