my $dbh = $DB->getDatabaseHandle();
return 'No database handle!' unless $dbh;
my $rows;
my $str;
my $dbrow;
my $queryText;
my $year = 2008;
my $month = 11;
my $limit = 10;

$queryText = "SELECT node.node_id,SUM(vote.weight) AS rep FROM vote,writeup,node WHERE vote.vote_id=writeup.writeup_id AND writeup.parent_e2node=node.node_id AND votetime LIKE '".$year."-".$month."%' AND node.title NOT LIKE '%, ".$year."' GROUP BY node.node_id ORDER BY rep DESC LIMIT ".$limit;
$rows = $dbh->prepare($queryText)
  or return $rows->errstr;
$rows->execute()
  or return $rows->errstr;
$str .= '<h3>e2nodes with the highest rep based on votes given this month (daylogs filtered)</h3>';
$str .= '<ul>';
while($dbrow = $rows->fetchrow_arrayref)
{
   $str .= '<li>'.linkNode($$dbrow[0]).' ('.$$dbrow[1].')</li>';
}
$str .= '</ul>';


$queryText = "SELECT vote.vote_id,SUM(vote.weight) AS rep FROM vote,node WHERE vote.vote_id=node.node_id AND node.type_nodetype=117 AND votetime LIKE '".$year."-".$month."%' AND node.createtime NOT LIKE '".$year."-".$month."%' GROUP BY vote_id ORDER BY rep DESC LIMIT ".$limit;
$rows = $dbh->prepare($queryText)
  or return $rows->errstr;
$rows->execute()
  or return $rows->errstr;
$str .= '<h3>Writeups with the highest rep based on votes given this month for writeups NOT written this month</h3>';
$str .= '<ul>';
while($dbrow = $rows->fetchrow_arrayref)
{
   $str .= '<li>'.linkNode($$dbrow[0]).' ('.$$dbrow[1].')</li>';
}
$str .= '</ul>';


$queryText = "SELECT vote.vote_id,SUM(vote.weight) AS rep FROM vote,node WHERE vote.vote_id=node.node_id AND node.type_nodetype=117 AND votetime LIKE '".$year."-".$month."%' AND node.createtime NOT LIKE '".$year."-".$month."%' GROUP BY vote_id ORDER BY rep LIMIT ".$limit;
$rows = $dbh->prepare($queryText)
  or return $rows->errstr;
$rows->execute()
  or return $rows->errstr;
$str .= '<h3>Writeups with the lowest rep based on votes given this month for writeups NOT written this month</h3>';
$str .= '<ul>';
while($dbrow = $rows->fetchrow_arrayref)
{
   $str .= '<li>'.linkNode($$dbrow[0]).' ('.$$dbrow[1].')</li>';
}
$str .= '</ul>';

return $str;





##############################################
# TEST FOR NEW WRITEUPS FEEDER (NOTE: IT DOESN'T WORK BECAUSE MIN() does not work the same in mySQL as it does in other DBs
##############################################

$queryText = "select min(writeup_id) from writeup order by writeup_id asc limit 50";
$rows = $dbh->prepare($queryText)
  or return $rows->errstr;
$rows->execute()
  or return $rows->errstr;
my $minwriteupid = $rows->fetchrow_array();
$str .= '<p>min writeup_id = '.$minwriteupid.'</p>';

my $queryText = "select
      wutypenode.title,
      writeup.writeup_id,
      writeup.notnew,
      wunode.author_user,
      wunode.reputation,
      parente2node.title,
      writeup.parent_e2node
   from writeup left join node as wunode on wunode.node_id = writeup.writeup_id
      left join node as wutypenode on wutypenode.node_id = writeup.wrtype_writeuptype
      left join node as parente2node on parente2node.node_id = writeup.parent_e2node
   where writeup_id >= (select min(writeup_id)
                           from writeup
                           where notnew=0
                           order by writeup_id desc limit 50)
   order by writeup_id desc";

$rows = $dbh->prepare($queryText)
  or return $rows->errstr;
$rows->execute()
  or return $rows->errstr;
my $rowCtr = 0;
my $unhiddenCtr = 0;
my $cssClass;
$str .= '<table>
         <tr>
         <th>#</th>
         <th>Title</th>
         <th>Type</th>
         <th>Author</th>
         <th>Hidden</th>
         <th>Rep</th>
         </tr>';

while($dbrow = $rows->fetchrow_arrayref)
{
  $rowCtr++;
  if ($$dbrow[2] == 0)
  {
    $unhiddenCtr++;
  }
  if ($rowCtr % 2 == 0)
  { 
    $cssClass = 'evenrow'; 
  }
  else
  { 
    $cssClass = 'oddrow';
  }
  $str .= '<tr class="'.$cssClass.'">
           <td>'.$rowCtr.'</td>
           <td>'.linkNode($$dbrow[6]).'</td>
           <td><a href="/index.pl?node_id='.$$dbrow[1].'">'.$$dbrow[0].'</a></td>
           <td>'.linkNode($$dbrow[3]).'</td>
           <td>'.$$dbrow[2].' ('.$unhiddenCtr.')</td>
           <td>'.$$dbrow[4].'</td>
           </tr>';
  if ($rowCtr >= 120){last;}
}
$str .= '</table>';
%]
