node.doctext:
[%

#my $isEdev = exists($HTMLVARS{'group_edev'}{$$USER{node_id}});
#return ("<p>Sorry, you are not in edev</p>") unless (isGod($USER) or $isEdev);

my $str = "<p>This is the list of HTML tags that have been approved for use in writeups.  ";
$str .= "Allowed attributes for the tags are listed below the tag name.  </p>";
$str .= "\n\n<dl>";
my $listapprovedtags = getNode('approved HTML tags', 'setting');
my $listvars = getVars($listapprovedtags);

##########
# Display the list of approved HTML tags

foreach my $keys (sort(keys(%{$listvars}))) {
$str .= "\n\t<dt>" . $keys . "</dt>";
$str .= "\n\t\t<dd>" . $listvars->{$keys} . "</dd>" unless ($listvars->{$keys} == 1);
}

return $str . "\n</dl>\n\n";

%]
