node.doctext:
[%

my $header = '<table align=center width=90%>';

my $footer = '</table>';


my $DATA = getNode "Nate's Wisdom Data",'restricted_superdoc';
my @wisdoms = split '\*', $$DATA{doctext};

my ($len, $wid) = (4, 2);
my %used;

local *getRand = sub {
   my $num;
   do {
     $num = int(rand(@wisdoms));
   } while (exists $used{$num});
  $used{$num} = 1;
  $num;
};

my $str;
foreach (1..$len) {
  $str.="<tr>";
  foreach (1..$wid) {
    $str.="<td>";
    my $wit = $wisdoms[getRand()];
    $wit =~ s/\n/\<br\>/gs;
    $str.=$wit;
    $str.="</td>";
  }
    $str.="</tr>\n";
}

$header. $str. $footer .
"<p align=right>Get some 
[Nate's Pseudo-Wisdoms|more wisdom]";
%]
