node.doctext:
[%

my $usertype = getId(getType('user'));

my $sql = "select count(*) from `node` where `type_nodetype` = $usertype and `createtime` > date_sub(now(), interval 1 year)";
my $usr = $dbh->prepare($sql);
$usr->execute or return 'Oh dear. Database oops';
my ($numUser) = $usr->fetchrow();

my $sql = "select count(*) from `user`, `node` where `user_id` = `node_id` and `createtime` > date_sub(now(), interval 1 week) and `lasttime` > 0";
my $usr = $dbh->prepare($sql);
$usr->execute or return 'Oh dear. Database oops';
my ($numLoggedIn) = $usr->fetchrow();

my $start = int($query->param('start'));
$start ||= 0;

#$sql = "select `user_id`, `nick`, `createtime`, `lasttime` from `user`, `node` where `node_id`=`user_id` and `createtime` > date_sub(now(), interval 1 week) order by createtime desc limit $start, 50";
$sql =  "select `user_id`, `nick`, `createtime`, `lasttime`, `notetext` from `node`, `user` left join `nodenote` on (`nodenote_nodeid` = `user_id`) where `node_id`=`user_id` and `createtime` > date_sub(now(), interval 1 week) group by (user.user_id) order by createtime desc limit $start, 50";

$usr = $dbh->prepare($sql);

$usr->execute or return 'Oh dear. Database oops';

my $row = 1;
my $last = $start + 50;
if ($last > $numUser) {$last = $numUser};

my $str = "<p>In the past week, $numUser users enrolled. Of those $numLoggedIn logged-in.</p>";
my $nav = "<p style='text-align: center'>";

if ($start > 0) {
   $nav .= '&laquo ' . linkNode($NODE, 'Later', {'start' => $start - 50}) . ' ';
}

for (my $i = 0; $i<$numUser; $i += 50) {   
   $nav .= ($i == $start) ? "<b>$i</b>" : linkNode($NODE, $i ? $i : 1, {'start' => $i});
   $nav .= ' ';
}

if ($last < $numUser) {
   $nav .= linkNode($NODE, 'Earlier', {'start' => $start + 50}) . ' &raquo;</p>';
}

$str .= $nav;

$str .= "<table style='width: 100%; border-top: 1px gray solid'><tr><th>Joined</th><th>User</th><th>Last logged in</th><th>Node note</th></tr>\n";
$usr->fetchrow_hashref;
while(my $N = $usr->fetchrow_hashref) {
	my $login = htmlcode('timesince',$$N{lasttime});
   my $create = htmlcode('timesince',$$N{createtime});

   $str .= sprintf("\t<tr class='%s'><td>%s</td><td><a href=%s>%s</a></td><td>%s</td><td>%s</td></tr>\n",
                  $row ? 'oddrow' : 'evenrow',
                  $create,
                  urlGen({'node_id'=>$$N{user_id}}),
                  $$N{nick},
                  $login eq '?' ? 'never' : $login,
                  $$N{notetext}
                  );
   $row= !$row;
}
$str .= '</table>' . $nav;
$str;
%]
