node.doctext:
[%

my $sessionId = htmlcode('getStarRatingSessionId');

my $csr=$DB->sqlSelectMany('starrating_id,rating_value', 'starrating', "rater_test_session_id = $sessionId ORDER BY rating_value DESC");

my $str;

#$str.="sessionId: $sessionId";

$str.="<table>\n";
my $count=0;

while (my $starStuff = $csr->fetchrow_hashref) {
  my $nodeId=$$starStuff{'starrating_id'};
  my $rating=$$starStuff{'rating_value'};
  $str.='<tr><td>'.htmlcode('stars',$nodeId,1,$count,10,2,$rating).'</td><td>'.linkNode($nodeId)."</td></tr>\n";
#  $str.="nodeId: $nodeId<br>";
#  $str.="rating: $rating";
}
$str.='</table>';
return $str;

%]
