node.doctext:
[%

use Everything::Room;


my $isCoolPerson = htmlcode('isinUsergroup',"$$USER{node_id},gods,1") ||
                   htmlcode('isinUsergroup',"$$USER{node_id},chanops,1");

if ($$VARS{lockedin} > time && !$isCoolPerson)
	{
	my $remainingtime = int( ($$VARS{lockedin} - time)/ 60 + 0.5);
	my $lockmessage = "<p><strong style='color:red;'>"
		. "You cannot change rooms for $remainingtime minutes.  "
		. "You can still send private messages, however, or talk to people in your current room.  "
		. "</strong></p>";
	return $lockmessage;
	}

return if getId($USER) == $HTMLVARS{guest_user};
changeRoom($USER,0);
'You step outside. You see many noders here.';
%]
