node.doctext:
[%
my $NG=getNode 'classic nodelets',"nodeletgroup";
my $str;
$str=linkNode($NG);
my $GROUP = $$NG{group};

my %enabled_nodelet_ids;
\@enabled_nodelet_ids{ split ",",$$VARS{nodelets} };

unless ($GROUP) {
	$str.="<i>This nodegroup is empty</i>";
} 
foreach my $N  (@$GROUP) {
  $N = $DB->getNodeById($N, 'nodelet');
  #$str.= "<li>" . linkNode ($N) . "($$N{type}{title})";
  next if exists $enabled_nodelet_ids{$$N{node_id}};
  $str.= insertNodelet($N);
}
return $str;
%]
