node.doctext:
[%
  my $V = getVars(getNode("boring heaven nodes", "setting"));
  my $nodelist;
  my $str;
  $str.="<maintenance>\n";

  foreach (keys %$V)
  {
    my $n = $_;
    $n =~ s/\_/ /g;
    my $node = getNode($n, "e2node");
    $str.="<e2link node_id=\"$$node{node_id}\">$$node{title}</e2link>\n";
  }

  $str.="</maintenance>\n";
  return $str;

%]
