node.doctext:
[%
my $str = "<?xml version=\"1.0\"?>\n";
$str.="<newwriteups>\n";

my $limit = $query->param("count");
$limit ||= "15";
$limit =~ s/[^\d]//g;
$limit = 300 if ($limit > 300);

my $csr = $DB->sqlSelectMany("node.node_id as nodeid", "newwriteup, node", "newwriteup.node_id=node.node_id and notnew=0 order by newwriteup_id DESC limit $limit");
while(my $row = $csr->fetchrow_hashref)
{
  my $n = getNodeById($$row{nodeid});
  next unless($n);
  my $author = getNodeById($$n{author_user});
  my $parent = getNodeById($$n{parent_e2node});
  my $wutype = getNodeById($$n{wrtype_writeuptype});

  $str.="<wu wrtype=\"$$wutype{title}\">";
  $str.="<e2link node_id=\"$$n{node_id}\">".encodeHTML($$n{title})."</e2link>";
  $str.="<author>";
  $str.="<e2link node_id=\"$$author{node_id}\">".encodeHTML($$author{title})."</e2link>" if $author;
  $str.="</author>";
  $str.="<parent>";
  $str.="<e2link node_id=\"$$parent{node_id}\">".encodeHTML($$parent{title})."</e2link>" if $parent;
  $str.="</parent>";

  $str.="</wu>";
}

$str.="</newwriteups>\n";
return $str;
%]
