node.doctext:
[%
my $str.="<vars>";

unless($HTMLVARS{guest_user} == $$NODE{node_id} ){
  my $ev = getVars(getNode("exportable vars","setting"));

  foreach(keys %$ev)
  {
    next unless $$VARS{$_};
    $str.="<key name=\"$_\">$$VARS{$_}</key>\n";
  }
}

$str.="</vars>";
return $str;
%]
