node.doctext:
[% 
my $str = "<?xml version=\"1.0\" encoding=\"UTF-8\" standalone=\"yes\"?>\n";
$str.="";


use Everything::Experience;
my $scratchview;

if($query->param("scratch"))
{
 $scratchview = getNode($query->param("scratch"), "user") unless($$USER{title} eq "Guest User");
}
$scratchview ||= $USER;

my $v = getVars($scratchview);
my $share = $$v{skratchShare};
$share ||= 0;


my $skratch = $DB->sqlSelect("scratch_text", "scratch", "scratch_id='$$scratchview{node_id}'");

$skratch = "" if $$USER{title} eq "Guest User";
$skratch = "" if $share == 0 and $$scratchview{node_id} != $$USER{user_id};
$skratch = parseLinks($skratch) unless($query->param('links_noparse'));

$str.="<scratchpad>\n";
$str.="<scratchtxt user=\"$$scratchview{title}\" public=\"$share\">".encodeHTML($skratch)."</scratchtxt>\n";
$str.="</scratchpad>\n";
return $str;
%]
