[%
my $str;

return '' if htmlcode('isSpider');

my $wherestr = 'for_user=0 and unix_timestamp(now())-unix_timestamp(tstamp) < 300';
$wherestr.=' and room='.$$USER{in_room};

my $csr = $DB->sqlSelectMany("*", "message", $wherestr);

while(my $row = $csr->fetchrow_hashref)
{
  my $aut = getNodeById($$row{author_user});
  $str.="<p>&lt;$$aut{title}&gt; ".encodeHTML($$row{msgtext})."</p>";
}

 $str.="<p align=\"center\"><a href=\"/index.pl?node_id=$$NODE{node_id}&amp;junk=".rand(1000).rand(1000).rand(1000)."\">refresh</a></p>";

return $str;
%]
